// Video
var video = document.getElementById("myVideo"),
    btn = document.getElementById("myBtn");
function myFunction() {
    video.paused ? (video.play(), (btn.innerHTML = "Pause")) : (video.pause(), (btn.innerHTML = "Play"));
}
// Menu Close
function menuClose() {
    if ($(window).width() < 992) {
        $(".navbar-nav li").on("click", function (e) {
            $(".navbar-toggler").click();
        });
            (window.onresize = function () {
                $(window).width() < 992 && $(".navbar-nav li").on("click", function (e) {
                    $(".navbar-toggler").click();
                });
            });
    }
}
// Menu Close
function menuClose() {
    if ($(window).width() < 992) {
        $(".menu-overlay").on("click", function (e) {
            $(".navbar-toggler").click();
        });
        (window.onresize = function () {
            $(window).width() < 992 && $(".menu-overlay").on("click", function (e) {
                $(".navbar-toggler").click();
            });
        });
    }
}

function onScroll(e) {
    var t = $(document).scrollTop();
    $(".navbar-nav a").each(function () {
        var e = $(this),
            o = $(e.attr("href"));
        o.position().top <= t && o.position().top + o.height() > t ? ($(".navbar-toggler ul li a").removeClass("active"), e.addClass("active")) : e.removeClass("active");
    });
}
$("a[href^='#section']").click(function (e) {
    e.preventDefault();
    var t = $($(this).attr("href")).offset().top - 50;
    $("body, html").animate({ scrollTop: t }, 1e3);
}),
    $(window).scroll(function () {
        $(this).scrollTop() > 80 ? $(".floating-header").addClass("sticky-header") : $(".floating-header").removeClass("sticky-header");
    }),
    setInterval(function () {
        menuClose();
    }, 500),
    $(document).on("scroll", onScroll),
    $('a[href^="#section"]').on("click", function (e) {
        e.preventDefault(),
            $(document).off("scroll"),
            $("a").each(function () {
                $(this).removeClass("active");
            }),
            $(this).addClass("active");
        var t = this.hash;
        ($target = $(t)),
            $("html, body")
                .stop()
                .animate({ scrollTop: $target.offset().top + 0 }, 500, "swing", function () {
                    (window.location.hash = t), $(document).on("scroll", onScroll);
                });
    });
var Swipes = new Swiper(".tabs-swiper", { slidesPerView: "auto", centeredSlides: !1, spaceBetween: 0, grabCursor: !0, loop: !1 });
Swipes = new Swiper(".healing-swiper", { slidesPerView: "auto", centeredSlides: !1, spaceBetween: 30, grabCursor: !0, loop: !1 });
